import React from "react";
import ThumbUpIcon from "@material-ui/icons/ThumbUp";
import ThumbDownIcon from "@material-ui/icons/ThumbDown";
import SideBarRow from "../SideBarRow/SideBarRow";
import ReplyIcon from "@material-ui/icons/Reply";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import PlaylistAddIcon from "@material-ui/icons/PlaylistAdd";
import "./style/VideoInfo.css";
import { Avatar, Button } from "@material-ui/core";

const VideoInfo = ({
  title,
  description,
  publishedDate,
  channelTitle,
  channelImage,
  viewCount,
  likeCount,
  dislikeCount,
  subs,
}) => {
  return (
    <div className="videoInfo">
      <div className="videoInfoHeadline">
        <h1>{title}</h1>
      </div>
      <div className="videoInfoStats">
        <p>
          {viewCount} views â€¢ {publishedDate}
        </p>
        <div className="videoInfoLikes">
          <SideBarRow Icon={ThumbUpIcon} title={likeCount} />
          <SideBarRow Icon={ThumbDownIcon} title={dislikeCount} />
          <SideBarRow Icon={ReplyIcon} title="SHARE" />
          <SideBarRow Icon={PlaylistAddIcon} title="SAVE" />
          <SideBarRow Icon={MoreHorizIcon} title="" />
        </div>
      </div>
      <hr />
      <div className="videoInfoChannel">
        <div>
          <Avatar
            className="videoInfoAvatar"
            alt={channelTitle}
            src={channelImage}
          />
          <div className="videoInfoChannelInfo">
            <h3 className="videoInfoChannelTitle">{channelTitle}</h3>
            <p className="videoInfoChannelSubs">{subs} subscribers</p>
          </div>
        </div>
        <div className="videoInfoSubscribe">
          <Button color="secondary">SUBSCRIBE</Button>
        </div>
      </div>
      <div className="videoInfo__channeldesc">
        <p>{description}</p>
      </div>
    </div>
  );
};

export default VideoInfo;
